tinymce.PluginManager.add('emoticons', function(editor, url) {
	function getHtml() {
		var emoticonsHtml;
		emoticonsHtml = '<table role="list" class="mce-grid">';
		tinymce.each(EMOTICONS, function(row) {
			emoticonsHtml += '<tr>';
			tinymce.each(row, function(icon) {
				var emoticonUrl = ROOT + 'public/emoticons/' + EMOTICON_DIR + '/' + icon;
				emoticonsHtml += '<td><a href="#" data-mce-url="' + emoticonUrl + '" data-mce-alt="' + icon + '" tabindex="-1" ' + 'role="option" aria-label="' + icon + '" style="padding: 5px;"><img src="' + emoticonUrl + '" style="width:16px;height:16px" role="presentation"></a></td>';
			});
			emoticonsHtml += '</tr>';
		});
		emoticonsHtml += '</table>';
		return emoticonsHtml;
	}

	editor.addButton('emoticons', {
		type: 'panelbutton',
		panel: {
			role: 'application',
			autohide: true,
			html: getHtml,
			onclick: function(e) {
				var linkElm = editor.dom.getParent(e.target, 'a');
				if (linkElm) {
					editor.insertContent(
						'<img src="' + linkElm.getAttribute('data-mce-url') + '" alt="' + linkElm.getAttribute('data-mce-alt') + '" style="width:16px;height:16px;vertical-align:text-bottom">'
					);
					this.hide();
				}
			}
		},
		tooltip: 'Emoticons'
	});
});
